mapper.select:
select * from smbms_user where
 userName like concat('%',#{username},'%') or userRole = #{roleId}
 limit ${begin},${size}

mapper.update:
update smbms_user
id = ${id}, userCode = #{userCode}, userName = #{userName}, userPassword = #{userPassword}, gender = #{gender}, birthday = #{birthday}, phone = #{phone}, address = #{address}, userRole = #{userRole}, createdBy = #{createdBy}, creationDate = #{creationDate}, modifyBy = #{modifyBy}, modifyDate = #{modifyDate}
where id = ${id}
